package wappy.client;import com.google.gwt.http.client.RequestBuilder;import com.google.gwt.http.client.RequestCallback;import com.google.gwt.http.client.RequestException;import com.google.gwt.http.client.Request;import com.google.gwt.http.client.Response;import com.google.gwt.json.client.JSONParser;import com.pathf.gwt.util.json.client.JSONWrapper;public class SimpleJSON {    public void onSuccess(JSONWrapper response) {    }    public void onFailure() {    }    public void query(String url, String data) {        RequestBuilder builder = new RequestBuilder(RequestBuilder.POST, url);        try {            builder.sendRequest(data, new RequestCallback() {                public void onError(Request request, Throwable exception) {                    onFailure();                }                public void onResponseReceived(Request request,                                               Response response) {                    if (response.getStatusCode() == 200) {                        onSuccess(new JSONWrapper(                                        JSONParser.parse(response.getText())));                    }                    else {                        onFailure();                    }                }            });        }        catch (RequestException e) {            onFailure();        }    }};