package wappy.client.mail;import com.extjs.gxt.ui.client.Style.HorizontalAlignment;import com.extjs.gxt.ui.client.event.ButtonEvent;import com.extjs.gxt.ui.client.event.SelectionListener;import com.extjs.gxt.ui.client.widget.MessageBox;import com.extjs.gxt.ui.client.widget.button.Button;import com.extjs.gxt.ui.client.widget.form.FormPanel;import com.extjs.gxt.ui.client.widget.form.TextField;import com.extjs.gxt.ui.client.widget.form.NumberField;import com.extjs.gxt.ui.client.widget.Window;import com.google.gwt.user.client.ui.RichTextArea;import com.google.gwt.user.client.Command;import com.google.gwt.user.client.DeferredCommand;import com.google.gwt.http.client.RequestBuilder;import com.google.gwt.http.client.RequestCallback;import com.google.gwt.http.client.RequestException;import com.google.gwt.http.client.Request;import com.google.gwt.http.client.Response;import com.google.gwt.json.client.JSONParser;import com.google.gwt.json.client.JSONObject;import com.google.gwt.json.client.JSONString;import com.pathf.gwt.util.json.client.JSONWrapper;public class SendMailDialog {    final Window window = new Window();    final FormPanel formPanel = new FormPanel();    final TextField<String> toField = new TextField<String>();    public SendMailDialog() {        window.setHeading("Send Mail");        window.setPlain(false);        window.setWidth(512);        window.setAutoHeight(true);        window.setResizable(true);		window.setModal(false);		window.setBlinkModal(false);        window.hide();		formPanel.setHeaderVisible(false);        formPanel.setButtonAlign(HorizontalAlignment.CENTER);        window.add(formPanel);		toField.setFieldLabel("To");		toField.setToolTip("Enter an e-mail address");		toField.setEmptyText("Enter an e-mail address");		toField.setAllowBlank(false);		formPanel.add(toField);        formPanel.add(new RichTextArea());		formPanel.addButton(            new Button("Send", new SelectionListener<ButtonEvent>() {			public void componentSelected(ButtonEvent ce) {                SendMailDialog.this.hide();			}		}));		formPanel.addButton(            new Button("Cancel", new SelectionListener<ButtonEvent>() {			public void componentSelected(ButtonEvent ce) {				SendMailDialog.this.hide();			}		}));    }    public void show() {        window.show();        DeferredCommand.addCommand(new Command() {            public void execute() {                window.toFront();            }        });    }    public void hide() {        window.hide();    }}